import React, { useEffect, useState } from 'react';

const MapWithMarkers = () => {
  const [markers, setMarkers] = useState([]);

  useEffect(() => {
    // Здесь ты можешь сделать запрос на сервер, чтобы получить координаты для маркеров
    // Предположим, что ты получаешь список координат в формате [{ lat: 55.753215, lng: 37.622504 }, { lat: 55.751999, lng: 37.617734 }]

    // Ниже приведен пример статичных координат для маркеров
    const dummyMarkers = [
      { lat: 55.753215, lng: 37.622504 },
      { lat: 55.751999, lng: 37.617734 }
    ];

    setMarkers(dummyMarkers);
  }, []);

  useEffect(() => {
    // Подключение скрипта API Яндекс Карт
    const ymaps = window.ymaps;
    if (ymaps) {
      ymaps.ready(initMap);
    } else {
      const script = document.createElement('script');
      script.type = 'text/javascript';
      script.src = 'https://api-maps.yandex.ru/2.1/?apikey=YOUR_API_KEY&lang=ru_RU';
      script.onload = () => {
        ymaps.ready(initMap);
      };
      document.body.appendChild(script);
    }
  }, []);

  const initMap = () => {
    const ymaps = window.ymaps;
    if (ymaps) {
      const myMap = new ymaps.Map('map', {
        center: [55.753215, 37.622504], // Москва
        zoom: 10
      });

      markers.forEach(marker => {
        const placemark = new ymaps.Placemark([marker.lat, marker.lng]);
        myMap.geoObjects.add(placemark);
      });
    }
  };

  return (
    <div id="map" style={{ width: '100%', height: '400px' }}></div>
  );
};

export default MapWithMarkers;
