import React, { useState } from 'react';
import './App.css'

const StationFilterModal = ({ isOpen, onClose, onApply }) => {
    const [filters, setFilters] = useState({
        all: false,
        connector1: false,
        connector2: false,
        connector3: false,
        slow: false,
        fast: false,
    });

    const handleCheckboxChange = (e) => {
        const { value, checked } = e.target;
        setFilters((prevFilters) => ({
            ...prevFilters,
            [value]: checked,
        }));
    };

    const handleSubmit = () => {
        // Передаем только те фильтры, которые выбраны
        const selectedFilters = Object.entries(filters)
            .filter(([key, value]) => value)
            .map(([key]) => key);
        onApply(selectedFilters); // Передаем выбранные фильтры
        onClose(); // Закрываем модальное окно после применения
    };

    const handleClearFilters = () => {
        setFilters({
            all: false,
            connector1: false,
            connector2: false,
            connector3: false,
            slow: false,
            fast: false,
        });
        onApply([]); // Очищаем фильтры
        onClose(); // Закрываем модальное окно
    };

    if (!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>Выберите критерии для отображения станций</h2>
                <div>
                    <label>
                        <input
                            type="checkbox"
                            value="all"
                            checked={filters.all}
                            onChange={handleCheckboxChange}
                        />
                        Показать все
                    </label>
                    <br></br>
                    <label>
                        <input
                            type="checkbox"
                            value="connector1"
                            checked={filters.connector1}
                            onChange={handleCheckboxChange}
                        />
                        Показать по количеству портов - 1
                    </label>
                    <br></br>
                    <label>
                        <input
                            type="checkbox"
                            value="connector2"
                            checked={filters.connector2}
                            onChange={handleCheckboxChange}
                        />
                        Показать по количеству портов - 2
                    </label>
                    <br></br>
                    <label>
                        <input
                            type="checkbox"
                            value="connector3"
                            checked={filters.connector3}
                            onChange={handleCheckboxChange}
                        />
                        Показать по количеству портов - 3
                    </label>
                    <br></br>
                    <label>
                        <input
                            type="checkbox"
                            value="slow"
                            checked={filters.slow}
                            onChange={handleCheckboxChange}
                        />
                        Показать медленные зарядные станции
                    </label>
                    <br></br>
                    <label>
                        <input
                            type="checkbox"
                            value="fast"
                            checked={filters.fast}
                            onChange={handleCheckboxChange}
                        />
                        Показать быстрые зарядные  станции
                    </label>
                </div>
                <button onClick={handleSubmit}>Применить</button>
                <button onClick={handleClearFilters}>Очистить фильтры</button>
                <button onClick={onClose}>Закрыть</button>
            </div>
        </div>
    );
};

export default StationFilterModal;
