import React, { useEffect, useState } from 'react';
import './App.css';

const YandexMap = () => {
  const [mapInitiated, setMapInitiated] = useState(false);

  useEffect(() => {
    const apiKey = 'a0325676-108c-4a26-8b08-8ddbcf9ee724';

    const initializeMap = () => {
      if (!window.ymaps) {
        const script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=${apiKey}&lang=ru_RU`;
        script.onload = () => {
          const mapContainer = document.getElementById('map');
          if (mapContainer && !mapInitiated) {
            mapContainer.innerHTML = '';

            window.ymaps.ready(() => {
              const map = new window.ymaps.Map("map", {
                center: [55.76, 37.64],
                zoom: 10,
              });
              map.controls.add(new window.ymaps.control.ZoomControl());
              map.controls.add(new window.ymaps.control.TypeSelector());
              map.controls.remove('searchControl');
              map.controls.remove('trafficControl');

              // Добавление отрисовки квадрата
              const g1 = 55.7558; // Пример для g1
              const g2 = 37.6176; // Пример для g2
              const g3 = 55.7537; // Пример для g3
              const g4 = 37.6201; // Пример для g4
              const rating = 0.8; // Пример для rating

              // Логика для раскраски квадрата
              const color = rating === 0 ? 'red' : 'green';

              const rectangle = new window.ymaps.GeoObject({
                geometry: {
                  type: 'Rectangle',
                  coordinates: [[g1, g2], [g3, g4]],
                },
                properties: {
                  hint: `Rating: ${rating}`,
                },
              }, {
                fillColor: color,
                strokeColor: '0000ff',
                opacity: 0.5,
              });

              map.geoObjects.add(rectangle);

              setMapInitiated(true);
            });
          }
        };
        document.body.appendChild(script);
      }
    };

    initializeMap();
  }, [mapInitiated]);

  return (
    <div className="body">
      {/* Шапка */}
      <div className="header">
        ШАПКА
      </div>
      {/* Меню */}
      <div className="map" id="map" style={{ width: '100%', height: '400px' }}></div>
    </div>
  );
};

export default YandexMap;
