const handleStationButtonClick = (objectsAdded1, fetchStationData, clearStations, mapRef, stationPlacemarks) => {
    if (!objectsAdded1.current) {
      fetchStationData(mapRef, stationPlacemarks);
      objectsAdded1.current = true;
    } else {
      clearStations(mapRef, stationPlacemarks);
      objectsAdded1.current = false;
    }
  };
  
  const fetchStationData = (mapRef, stationPlacemarks) => {
    if (!mapRef.current || !stationPlacemarks.current) {
      console.error('mapRef or stationPlacemarks is undefined');
      return;
    }
  
    fetch('http://localhost:5000/api/Station')
      .then(response => response.json())
      .then(data => {
        data.forEach(coord => {
          const placemark = new window.ymaps.Placemark(
            [coord.latitude, coord.longitude],
            { hintContent: coord.address }
          );
  
          stationPlacemarks.current.push(placemark);
          mapRef.current.geoObjects.add(placemark);
        });
      })
      .catch(error => {
        console.error('Error fetching station data:', error);
      });
  };
  
  const clearStations = (mapRef, stationPlacemarks) => {
    if (!mapRef.current || !stationPlacemarks.current) {
      console.error('mapRef or stationPlacemarks is undefined');
      return;
    }
  
    stationPlacemarks.current.forEach((placemark) => {
      mapRef.current.geoObjects.remove(placemark);
    });
    stationPlacemarks.current = [];
  };
  
  export { handleStationButtonClick, fetchStationData, clearStations };
