import React, { useState } from 'react';
import config from './config';

const Settings = ({ onSaveSettings }) => {
  const [color, setColor] = useState(config.polygonColor);
  const [opacity, setOpacity] = useState(config.polygonOpacity);

  const handleColorChange = (e) => {
    setColor(e.target.value);
  };

  const handleOpacityChange = (e) => {
    setOpacity(e.target.value);
  };

  const handleSave = () => {
    const newConfig = { polygonColor: color, polygonOpacity: opacity };
    onSaveSettings(newConfig);
  };

  return (
    <div>
      <label>
        Цвет:
        <input type="color" value={color} onChange={handleColorChange} />
      </label>
      <br />
      <label>
        Прозрачность:
        <input
          type="range"
          min="0"
          max="1"
          step="0.1"
          value={opacity}
          onChange={handleOpacityChange}
        />
      </label>
      <br />
      <button onClick={handleSave}>Сохранить настройки</button>
    </div>
  );
};

export default Settings;
