import React, { useEffect, useState, useRef } from 'react';
import './App.css';

const YandexMap = () => {
  const [mapInitiated, setMapInitiated] = useState(false);
  const mapRef = useRef(null); // Создаем реф для хранения объекта карты

  useEffect(() => {
    const apiKey = 'a0325676-108c-4a26-8b08-8ddbcf9ee724';

    const initializeMap = () => {
      if (typeof window.ymaps === 'undefined') {
        const script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=${apiKey}&lang=ru_RU`;
        script.onload = () => {
          if (mapInitiated || !mapRef.current) return;

          window.ymaps.ready(() => {
            mapRef.current = new window.ymaps.Map("map", {
              center: [55.76, 37.64],
              zoom: 10,
              controls: []
            });

            // Добавляем только нужные контролы
            mapRef.current.controls.add(new window.ymaps.control.ZoomControl());
            mapRef.current.controls.add(new window.ymaps.control.TypeSelector());
            mapRef.current.controls.remove('searchControl');
          });
        };
        document.body.appendChild(script);
      } else {
        if (!mapRef.current && mapInitiated) {
          initializeMap();
        }
      }
    };

    initializeMap();
  }, [mapInitiated]);

  const handleStationButtonClick = () => {
    fetch('http://localhost:5000/api/Station')
      .then(response => response.json())
      .then(data => {
        if (mapRef.current) {
          data.forEach(coord => {
            const placemark = new window.ymaps.Placemark(
              [coord.latitude, coord.longitude],
              {
                hintContent: coord.address,
              }
            );

            mapRef.current.geoObjects.add(placemark);
          });
        }
      })
      .catch(error => {
        console.error('Error fetching station data:', error);
      });
    setMapInitiated(true);
  };

  const handleZoneButtonClick = () => {
    fetch('http://localhost:5000/api/Zone')
      .then(response => response.json())
      .then(data => {
        if (mapRef.current) {
          data.forEach(item => {
            const g1 = item.g1;
            const g2 = item.g2;
            const g3 = item.g3;
            const g4 = item.g4;
            const rating = item.rating;

            const color = rating === 0 ? 'rgba(255, 0, 0, 1)' : 'rgba(0, 255, 0, 1)';

            const rectangle = new window.ymaps.GeoObject({
              geometry: {
                type: 'Rectangle',
                coordinates: [[g1, g2], [g3, g4], [g1, g4], [g3, g2]],
              },
              properties: {
                hintContent: `Rating: ${rating}`,
              },
            }, {
              fillColor: color,
              strokeColor: '#0000ff',
              opacity: 0.2,
            });
            mapRef.current.geoObjects.add(rectangle);
          });
        }
      })
      .catch(error => {
        console.error('Error fetching zone data:', error);
      });
  };

  return (
    <div className="body">
      {/* Шапка */}
      <div className="header">
        ШАПКА
      </div>
      {/* Меню */}
      <div className="controls">
        <button onClick={handleStationButtonClick}>Показать станции</button>
        <button onClick={handleZoneButtonClick}>Показать зоны</button>
      </div>
      <div className="map" id="map" style={{ width: '100%', height: '400px' }}></div>
    </div>
  );
};

export default YandexMap;
