import React, { useState } from 'react';


const StationFilterModal = ({ isOpen, onClose, onApply }) => {
    const [selectedFilter, setSelectedFilter] = useState(null);

    const handleChange = (e) => {
        setSelectedFilter(e.target.value);
    };

    const handleSubmit = () => {
        onApply(selectedFilter);
    };

    if (!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>Выберите критерии для отображения станций</h2>
                <div>
                    <label>
                        <input type="radio" value="all" checked={selectedFilter === 'all'} onChange={handleChange} />
                        Показать все
                    </label>
                    <label>
                        <input type="radio" value="1" checked={selectedFilter === '1'} onChange={handleChange} />
                        Показать по количеству портов - 1
                    </label>
                    <label>
                        <input type="radio" value="2" checked={selectedFilter === '2'} onChange={handleChange} />
                        Показать по количеству портов - 2
                    </label>
                    <label>
                        <input type="radio" value="3" checked={selectedFilter === '3'} onChange={handleChange} />
                        Показать по количеству портов - 3
                    </label>
                    <label>
                        <input type="radio" value="slow" checked={selectedFilter === 'slow'} onChange={handleChange} />
                        Показать медленные зарядные устройства
                    </label>
                    <label>
                        <input type="radio" value="fast" checked={selectedFilter === 'fast'} onChange={handleChange} />
                        Показать быстрые зарядные устройства
                    </label>
                </div>
                <button onClick={handleSubmit}>Применить</button>
                <button onClick={onClose}>Закрыть</button>
            </div>
        </div>
    );
};

export default StationFilterModal;
