import React, { useEffect, useState } from 'react';
import './App.css';

const YandexMap = () => {
  const [mapInitiated, setMapInitiated] = useState(false);

  useEffect(() => {
    let script;
    const apiKey = 'a0325676-108c-4a26-8b08-8ddbcf9ee724';

    const initializeMap = () => {
      if (!window.ymaps) {
        script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=${apiKey}&lang=ru_RU`;
        script.onload = () => {
          setMapInitiated(true);
        };
        document.body.appendChild(script);
      }
    };

    initializeMap();

    return () => {
      if (script) document.body.removeChild(script);
    };

  }, []);

  useEffect(() => {
    if (!mapInitiated) return;

    const fetchData = async (url) => {
      try {
        const response = await fetch(url);
        if (!response.ok) throw new Error('Network response was not ok.');
        const data = await response.json();
        return data;
      } catch (error) {
        console.error('Error fetching data:', error);
        return null;
      }
    };

    const fetchDataAndHandleResponse = async (url, processData) => {
      const data = await fetchData(url);
      if (!data) return;
      processData(data);
    };

    const handleStationData = (data) => {
      map.geoObjects.removeAll();
      data.forEach(coord => {
        const placemark = new window.ymaps.Placemark(
          [coord.latitude, coord.longitude],
          {
            hintContent: coord.address,
          }
        );
        map.geoObjects.add(placemark);
      });
    };

    const handleZoneData = (data) => {
      data.forEach(item => {
        const { g1, g2, g3, g4, rating } = item;
        const color = rating === 0 ? { r: 255, g: 0, b: 0, a: 1 } : { r: 0, g: 255, b: 0, a: 1 };
        const rectangle = new window.ymaps.GeoObject({
          geometry: {
            type: 'Rectangle',
            coordinates: [[g2, g1], [g4, g3]],
          },
          properties: {
            hintContent: `Rating: ${rating}`,
          },
        }, {
          fillColor: `rgba(${color.r}, ${color.g}, ${color.b}, ${color.a})`,
          strokeColor: '0000ff',
          opacity: 0.5,
        });
        map.geoObjects.add(rectangle);
      });
    };

    const map = new window.ymaps.Map("map", {
      center: [55.76, 37.64],
      zoom: 10,
    });
    map.controls.add(new window.ymaps.control.ZoomControl());
    map.controls.add(new window.ymaps.control.TypeSelector());
    map.controls.remove('searchControl');
    map.controls.remove('trafficControl');

    fetchDataAndHandleResponse('http://localhost:5000/api/Station', handleStationData);
    fetchDataAndHandleResponse('http://localhost:5000/api/Zone', handleZoneData);
  }, [mapInitiated]);

  return (
    <div className="body">
      {/* Шапка */}
      <div className="header">
        ШАПКА
      </div>
      {/* Меню */}
      <div className="map" id="map" style={{ width: '100%', height: '400px' }}></div>
    </div>
  );
};

export default YandexMap;
