  const handleZoneButtonClick = () => {
    if (objectsAdded.current) {
      clearMap();
      objectsAdded.current = false;
    } else {
      fetchZoneData();
      objectsAdded.current = true;
    }
    setShowControls(false);
  };

  const fetchZoneData = () => {
    // Fetch zone data and add rectangles to the map
  };

  const clearMap = () => {
    // Remove placemarks and rectangles from the map
  };

  const handleControlsToggle = () => {
    setShowControls(!showControls);
  };

  return (
    <div className="body">

      {showControls && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="toggleControls" onClick={handleControlsToggle}>
              {showControls ? 'Свернуть' : 'Развернуть'}
            </div>
            <div className="controlButton" onClick={handleStationButtonClick}>
              Показать станции
            </div>
            <div className="controlButton" onClick={handleZoneButtonClick}>
              Показать зоны
            </div>
          </div>
          <div className="map" id="map" style={{ width: '100%', height: '100vh' }}></div>
        </div>
      )}
    </div>
  );
};

export default YandexMap;
