export const handleStationButtonClick = (mapRef, stationPlacemarks, filter) => {
  if (stationPlacemarks.current.length === 0) {
      fetchStationData(mapRef, stationPlacemarks, filter);
  } else {
      clearStations(mapRef, stationPlacemarks);
  }
};

const fetchStationData = (mapRef, stationPlacemarks, filter) => {
  fetch('http://localhost:5000/api/Station')
      .then(response => response.json())
      .then(data => {
          // Фильтрация данных в зависимости от выбранного критерия
          const filteredData = data.filter(coord => {
              switch (filter) {
                  case 'all':
                      return true;
                  case '1':
                      return coord.connectors_total === 1;
                  case '2':
                      return coord.connectors_total === 2;
                  case '3':
                      return coord.connectors_total === 3;
                  case 'slow':
                      return coord.evse_type === 0;
                  case 'fast':
                      return coord.evse_type === 1;
                  default:
                      return true;
              }
          });

          filteredData.forEach(coord => {
              const placemark = new window.ymaps.Placemark(
                  [coord.latitude, coord.longitude],
                  { hintContent: coord.adress }
              );

              stationPlacemarks.current.push(placemark);
              mapRef.current.geoObjects.add(placemark);
          });
      })
      .catch(error => {
          console.error('Error fetching station data:', error);
      });
};
  
  const clearStations = (mapRef, objectsAdded1) => {
    objectsAdded1.current.forEach((placemark) => {
      mapRef.current.geoObjects.remove(placemark);
    });
    objectsAdded1.current = [];
  };
