import React, { useEffect, useState, useRef } from 'react';
import './App.css';

const YandexMap = () => {
  const [mapInitiated, setMapInitiated] = useState(false);
  const mapRef = useRef(null);
  const objectsAdded = useRef(false);

  useEffect(() => {
    const apiKey = 'a0325676-108c-4a26-8b08-8ddbcf9ee724';

    const initializeMap = () => {
      if (!window.ymaps) {
        const script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=${apiKey}&lang=ru_RU`;
        script.onload = () => {
          if (mapInitiated) return;

          const mapContainer = document.getElementById('map');
          if (mapContainer) {
            mapContainer.innerHTML = '';

            window.ymaps.ready(() => {
              mapRef.current = new window.ymaps.Map("map", {
                center: [55.76, 37.64],
                zoom: 10,
                controls: [] 
              });

              mapRef.current.controls.add('zoomControl', { position: { top: 10, left: 10 } });
              mapRef.current.controls.add('typeSelector', { position: { top: 10, right: 10 } });

              setMapInitiated(true);
            });
          }
        };

        if (!document.querySelector('script[src^="https://api-maps.yandex.ru/2.1/"]')) {
          document.body.appendChild(script);
        }
      }
    };

    initializeMap();
  }, [mapInitiated]);

  const handleStationButtonClick = () => {
    if (objectsAdded.current) {
      clearMap();
      objectsAdded.current = false;
    } else {
      fetchStationData();
      objectsAdded.current = true;
    }
  };

  const fetchStationData = () => {
    fetch('http://localhost:5000/api/Station')
      .then(response => response.json())
      .then(data => {
        data.forEach(coord => {
          const placemark = new window.ymaps.Placemark(
            [coord.latitude, coord.longitude],
            { hintContent: coord.address }
          );

          mapRef.current.geoObjects.add(placemark);
        });
      })
      .catch(error => {
        console.error('Error fetching station data:', error);
      });
  };

  const handleZoneButtonClick = () => {
    if (objectsAdded.current) {
      clearMap();
      objectsAdded.current = false;
    } else {
      fetchZoneData();
      objectsAdded.current = true;
    }
  };

  const fetchZoneData = () => {
    fetch('http://localhost:5000/api/Zone')
      .then(response => response.json())
      .then(data => {
        data.forEach(item => {
          const { g1, g2, g3, g4, rating } = item;
          const color = rating === 0 ? 'rgba(255, 0, 0, 1)' : 'rgba(0, 255, 0, 1)';

          const rectangle = new window.ymaps.GeoObject({
            geometry: {
              type: 'Rectangle',
              coordinates: [[g1, g2], [g3, g4], [g1, g4], [g3, g2]],
            },
            properties: { hintContent: `Rating: ${rating}` },
          }, {
            fillColor: color,
            strokeColor: '#0000ff',
            opacity: 0.2,
          });

          mapRef.current.geoObjects.add(rectangle);
        });
      })
      .catch(error => {
        console.error('Error fetching zone data:', error);
      });
  };

  const clearMap = () => {
    mapRef.current.geoObjects.each((geoObject) => {
      mapRef.current.geoObjects.remove(geoObject);
    });
  };
  return (
    <div className="body">
      <div className="header">ШАПКА</div>
      <div className="controls">
        <button onClick={handleStationButtonClick}>Показать станции</button>
        <button onClick={handleZoneButtonClick}>Показать зоны</button>
      </div>
      <div className="map" id="map" style={{ width: '100%', height: '400px' }}></div>
    </div>
  );
};

export default YandexMap;
