import React, { useEffect, useState } from 'react';
import './App.css';

const YandexMap = () => {
  const [map, setMap] = useState(null);

  useEffect(() => {
    const apiKey = 'a0325676-108c-4a26-8b08-8ddbcf9ee724';

    const loadMapScript = () => {
      if (!window.ymaps) {
        const script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=${apiKey}&lang=ru_RU`;
        script.onload = () => {
          initMap();
        };
        document.body.appendChild(script);
      }
    };

    const initMap = () => {
      const ymaps = window.ymaps;
      const newMap = new ymaps.Map("map", {
        center: [55.76, 37.64],
        zoom: 10,
      });

      newMap.controls.add(new ymaps.control.ZoomControl());
      newMap.controls.add(new ymaps.control.TypeSelector());
      newMap.controls.remove('searchControl');
      newMap.controls.remove('trafficControl');

      setMap(newMap);
    };

    loadMapScript();

    return () => {
      if (map) {
        map.destroy(); // Очищаем карту при размонтировании компонента
      }
    };
  }, [map]);

  useEffect(() => {
    if (!map) return;

    const fetchData = async (url) => {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Network response was not ok.');
        }
        return await response.json();
      } catch (error) {
        console.error('Error fetching data:', error);
        return null;
      }
    };

    const fetchAndHandleData = async (url, processData) => {
      const data = await fetchData(url);

      if (data) {
        processData(data);
      }
    };

    const handleStationData = (data) => {
      map.geoObjects.removeAll();

      data.forEach(coord => {
        const placemark = new window.ymaps.Placemark(
          [coord.latitude, coord.longitude],
          {
            hintContent: coord.address,
          }
        );

        map.geoObjects.add(placemark);
      });
    };

    const handleZoneData = (data) => {
      data.forEach(item => {
        const { g1, g2, g3, g4, rating } = item;
        const color = rating === 0 ? { r: 255, g: 0, b: 0, a: 1 } : { r: 0, g: 255, b: 0, a: 1 };

        const rectangle = new window.ymaps.GeoObject({
          geometry: {
            type: 'Rectangle',
            coordinates: [[g2, g1], [g4, g3]],
          },
          properties: {
            hintContent: `Rating: ${rating}`,
          },
        }, {
          fillColor: `rgba(${color.r}, ${color.g}, ${color.b}, ${color.a})`,
          strokeColor: '0000ff',
          opacity: 0.5,
        });

        map.geoObjects.add(rectangle);
      });
    };

    fetchAndHandleData('http://localhost:5000/api/Station', handleStationData);
    fetchAndHandleData('http://localhost:5000/api/Zone', handleZoneData);
  }, [map]);

  return (
    <div className="body">
      {/* Шапка */}
      <div className="header">
        ШАПКА
      </div>
      {/* Меню */}
      <div className="map" id="map" style={{ width: '100%', height: '400px' }}></div>
    </div>
  );
};

export default YandexMap;
