import React, { useEffect, useState } from 'react';
import './App.css';

const YandexMap = () => {
  const [mapInitiated, setMapInitiated] = useState(false);

  useEffect(() => {
    const apiKey = 'a0325676-108c-4a26-8b08-8ddbcf9ee724';

    const initializeMap = () => {
      if (!window.ymaps) {
        const script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=${apiKey}&lang=ru_RU`;
        script.onload = () => {
          const mapContainer = document.getElementById('map');
          if (mapContainer && !mapInitiated) {
            mapContainer.innerHTML = '';

            window.ymaps.ready(() => {
              const map = new window.ymaps.Map("map", {
                center: [55.76, 37.64],
                zoom: 10,
              });
              map.controls.add('zoomControl');
              map.controls.add('typeSelector');
              map.controls.remove('searchControl');
              map.controls.remove('trafficControl');

              fetch('http://localhost:5000/api/Station') 
                .then(response => response.json())
                .then(data => {
                  data.forEach(coord => {
                    const placemark = new window.ymaps.Placemark(
                      [coord.latitude, coord.longitude],
                      {
                        hintContent: coord.adress,
                      }
                    );
                    map.geoObjects.add(placemark);
                  });
                });


              setMapInitiated(true);
              fetch('http://localhost:5000/api/Zone')
                .then(response => response.json())
                .then(data => {
                  data.forEach(item => {
                  const g1 = item.g1;
                  const g2 = item.g2;
                  const g3 = item.g3;
                  const g4 = item.g4;
                  const rating = item.rating;

                  const coordinates = [
                    [g1, g2],
                    [g3, g4],
                    [g3, g4],
                    [g1, g4]
                  ];

                  const opacity = rating;

                  const color = rating < 0.5 ? 'red' : 'green';

                  const polygon = new window.ymaps.Polygon([coordinates], {
                  hintContent: `Rating: ${rating}`,
                }, {
                      fillColor: color,
                      strokeColor: '#0000FF',
                      opacity: opacity,
                      strokeWidth: 5
                    });

                    map.geoObjects.add(polygon);
                   });
                      })
                        .catch(error => {
                          console.error('Error fetching data:', error);
                      });
                    });
                  }
        };
        document.body.appendChild(script);
      }

    };

    initializeMap();
  }, [mapInitiated]);

  return (
    <div className="body">
      {/* Шапка */}
      <div className="header">
        ШАПКА
      </div>
      {/* Меню */}
      <div className="map" id="map" style={{ width: '100%', height: '400px' }}></div>
    </div>
  );
};

export default YandexMap;
