import React, { useState, useRef, useEffect } from 'react';
import Settings from './settings';
import config from './config';
import { handleZoneButtonClick, clearZones, fetchZoneData, processPolygon, getRatingColor } from './zoneHandler'; // предполагаем, что эти функции находятся в отдельном файле
import { handleStationButtonClick } from './StationHandler'
import {handleFileInputChange, handleFileInputChange2} from './addFiles'
const YandexMap = () => {
  const [mapInitiated, setMapInitiated] = useState(false);
  const mapRef = useRef(null);
  const stationPlacemarks = useRef([]);
  const [zoneRectangles, setZoneRectangles] = useState([]);
  const [objectsAdded, setObjectsAdded] = useState(false);
  const [settings, setSettings] = useState(config);

  useEffect(() => {
    const apiKey = 'a0325676-108c-4a26-8b08-8ddbcf9ee724';
    const initializeMap = () => {
      if (!window.ymaps) {
        const script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=${apiKey}&lang=ru_RU`;
        script.onload = () => {
          if (mapInitiated) return;
          const mapContainer = document.getElementById('map');
          if (mapContainer) {
            mapContainer.innerHTML = '';
            window.ymaps.ready(() => {
              mapRef.current = new window.ymaps.Map("map", {
                center: [55.76, 37.64],
                zoom: 10,
                controls: []
              });
              mapRef.current.controls.add('zoomControl', { position: { top: 10, left: 10 } });
              mapRef.current.controls.add('typeSelector', { position: { top: 10, right: 10 } });
              setMapInitiated(true);
            });
          }
        };
        if (!document.querySelector('script[src^="https://api-maps.yandex.ru/2.1/"]')) {
          document.body.appendChild(script);
        }
      }
    };
    initializeMap();
  }, [mapInitiated]);

  const handleSaveSettings = (newSettings) => {
    setSettings(newSettings);
    // Сохраним настройки в localStorage для демонстрации сохранения в файл
    localStorage.setItem('mapSettings', JSON.stringify(newSettings));
  };

  const handleZoneButtonClick1 = () => {
    handleZoneButtonClick(mapRef, zoneRectangles, setZoneRectangles, objectsAdded, setObjectsAdded, settings);
  };

  const handleStationButtonClick1 = () => {
    handleStationButtonClick(mapRef, stationPlacemarks);
  };

  return (
    <div className="body">
      <div className="header">
        Добавте ЭЗС:
        <input type="file" className="header-button" onChange={handleFileInputChange} />
        <br />
        Добавте зоны:
        <input type="file" className="header-button" onChange={handleFileInputChange2} />
      </div>
      <div className="content">
        <div className="controls">
          <div className='baton' onClick={handleStationButtonClick1}>Показать станции</div>
          <div className='baton' onClick={handleZoneButtonClick1}>Показать зоны</div>
        </div>
        <Settings onSaveSettings={handleSaveSettings} />
        <div className="map" id="map"></div>
      </div>
    </div>
  );
};

export default YandexMap;
