import React, { useEffect, useState, useRef } from 'react';
import './App.css';

const YandexMap = () => {
  const [mapInitiated, setMapInitiated] = useState(false);
  const mapRef = useRef(null);
  const objectsAdded = useRef(false);
  const objectsAdded1 = useRef(false);
  const stationPlacemarks = useRef([]);
  const zoneRectangles = useRef([]);

  useEffect(() => {
    const apiKey = 'a0325676-108c-4a26-8b08-8ddbcf9ee724';

    const initializeMap = () => {
      if (!window.ymaps) {
        const script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=${apiKey}&lang=ru_RU`;
        script.onload = () => {
          if (mapInitiated) return;

          const mapContainer = document.getElementById('map');
          if (mapContainer) {
            mapContainer.innerHTML = '';

            window.ymaps.ready(() => {
              mapRef.current = new window.ymaps.Map("map", {
                center: [55.76, 37.64],
                zoom: 10,
                controls: []
              });

              mapRef.current.controls.add('zoomControl', { position: { top: 10, left: 10 } });
              mapRef.current.controls.add('typeSelector', { position: { top: 10, right: 10 } });

              setMapInitiated(true);
            });
          }
        };

        if (!document.querySelector('script[src^="https://api-maps.yandex.ru/2.1/"]')) {
          document.body.appendChild(script);
        }
      }
    };

    initializeMap();
  }, [mapInitiated]);

  const handleStationButtonClick = () => {
    if (!objectsAdded1.current) {
      fetchStationData();
      objectsAdded1.current = true;
    } else {
      clearStations();
      objectsAdded1.current = false;
    }
  };


  const fetchStationData = () => {
    fetch('http://localhost:5000/api/Station')
      .then(response => response.json())
      .then(data => {
        data.forEach(coord => {
          const placemark = new window.ymaps.Placemark(
            [coord.latitude, coord.longitude],
            { hintContent: coord.address }
          );

          stationPlacemarks.current.push(placemark);
          mapRef.current.geoObjects.add(placemark);
        });
      })
      .catch(error => {
        console.error('Error fetching station data:', error);
      });
  };

  const handleZoneButtonClick = () => {
    if (!objectsAdded.current) {
      fetchZoneData();
      objectsAdded.current = true;
    } else {
      clearZones();
      objectsAdded.current = false;
    }
  };



  const fetchZoneData = () => {
    fetch('http://localhost:5000/api/Zone')
      .then(response => response.json())
      .then(data => {
        data.forEach(item => {
          const { polygon, rating } = item;

          // Преобразование WKB в координаты
          const coordinates = parseWKB(polygon);

          let color = 'rgba(0, 255, 0, 0.5)'; // Зеленый цвет по умолчанию
          if (rating === 0) {
            color = 'rgba(255, 0, 0, 0.5)'; // Красный цвет для rating = 0
          } else if (rating > 0 && rating < 0.5) {
            color = 'rgba(255, 165, 0, 0.5)'; // Оранжевый цвет для rating от 0 до 0.5
          }

          const polygonGeoObject = new window.ymaps.Polygon([coordinates], {}, {
            fillColor: color,
            strokeColor: '#0000ff',
            opacity: 0.5,
          });

          mapRef.current.geoObjects.add(polygonGeoObject);
        });
      })
      .catch(error => {
        console.error('Ошибка при получении данных полигонов:', error);
      });   
  };

  // Функция для преобразования WKB в координаты
  function parseWKB(wkb) {
    let wkbArray;
    if (typeof wkb === 'string') {
        wkbArray = new Uint8Array(wkb.match(/.{2}/g).map(byte => parseInt(byte, 16))); // Преобразование шестнадцатеричной строки в массив байтов
    } else if (Array.isArray(wkb) && wkb.every(item => typeof item === 'number')) {
        wkbArray = new Uint8Array(wkb); // Если wkb массив чисел, считаем, что он уже в нужном формате
    } else {
        console.error('WKB должен быть строкой или массивом чисел');
        return [];
    }

    function readWKB(wkbArray, offset) {
        let type = wkbArray[offset];
        let coordinates = [];
        offset += 1;

        if (type == 1) { // Точка
            let x = new Float64Array(wkbArray.slice(offset, offset + 8).buffer)[0];
            let y = new Float64Array(wkbArray.slice(offset + 8, offset + 16).buffer)[0];
            coordinates.push([x, y]);
        } else if (type == 2) { // Линия
            let numPoints = new Uint32Array(wkbArray.slice(offset, offset + 4).buffer)[0];
            offset += 4;

            for (let i = 0; i < numPoints; i++) {
                let x = new Float64Array(wkbArray.slice(offset, offset + 8).buffer)[0];
                let y = new Float64Array(wkbArray.slice(offset + 8, offset + 16).buffer)[0];
                coordinates.push([x, y]);
                offset += 16;
            }
        } else if (type == 3) { // Полигон
            let numRings = new Uint32Array(wkbArray.slice(offset, offset + 4).buffer)[0];
            offset += 4;

            for (let i = 0; i < numRings; i++) {
                let numPoints = new Uint32Array(wkbArray.slice(offset, offset + 4).buffer)[0];
                offset += 4;
                let ring = [];

                for (let j = 0; j < numPoints; j++) {
                    let x = new Float64Array(wkbArray.slice(offset, offset + 8).buffer)[0];
                    let y = new Float64Array(wkbArray.slice(offset + 8, offset + 16).buffer)[0];
                    ring.push([x, y]);
                    offset += 16;
                }

                coordinates.push(ring);
            }
        }

        return coordinates;
    }

    return readWKB(wkbArray, 0);
}





  const clearStations = () => {
    stationPlacemarks.current.forEach((placemark) => {
      mapRef.current.geoObjects.remove(placemark);
    });
    stationPlacemarks.current = [];
  };

  const clearZones = () => {
    zoneRectangles.current.forEach((rectangle) => {
      mapRef.current.geoObjects.remove(rectangle);
    });
    zoneRectangles.current = [];
  };

  return (
    <div className="body">
      <div className="header">ШАПКА
      <input
          type="file"
          className="header-button"
          //onChange={handleFileInputChange}

        />
        <input
          type="file"
          className="header-button"
          //onChange={}

        />
      </div>
      <div className='content'>
      <div className="controls">
        <div className='baton' onClick={handleStationButtonClick}>Показать станции</div>
        <div className='baton' onClick={handleZoneButtonClick}>Показать зоны</div>
      </div>
      <div className="map" id="map"></div>
      </div>
    </div>
  );
};

export default YandexMap;
