import React, { useState } from 'react';

const SettingsModal = ({ isOpen, onClose, onSaveSettings, onUpdatePolygons }) => {
  const [color0, setColor0] = useState('rgba(255, 0, 0, 0.5)');
  const [color0_5, setColor0_5] = useState('rgba(255, 255, 0, 0.5)');
  const [color5, setColor5] = useState('rgba(0, 255, 0, 0.5)');
  const [opacity, setOpacity] = useState(0.5);

  const handleSave = () => {
    const settings = {
      color0,
      color0_5,
      color5,
      opacity,
    };
    // Сохранение настроек в CSV
    const csvContent = `data:text/csv;charset=utf-8,Рейтинг,Цвет,Прозрачность\n0,${color0},${opacity}\n0-0.5,${color0_5},${opacity}\n5+,${color5},${opacity}`;
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", "polygon_settings.csv");
    document.body.appendChild(link); // нужно для Firefox
    link.click();
  };

  const handleImport = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const content = e.target.result;
        const rows = content.split('\n').slice(1); // Игнорируем заголовок
        const settings = {};
        rows.forEach(row => {
          const [rating, color, opacity] = row.split(',');
          if (rating === '0') {
            setColor0(color);
          } else if (rating === '0-0.5') {
            setColor0_5(color);
          } else if (rating === '5+') {
            setColor5(color);
          }
          setOpacity(opacity); // Предположим, что для всех одинаковая прозрачность
        });
      };
      reader.readAsText(file);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h2>Настройки полигонов</h2>
        <div>
          <label>
            Цвет для рейтинга 0:
            <input type="color" value={color0} onChange={(e) => setColor0(e.target.value)} />
          </label>
        </div>
        <div>
          <label>
            Цвет для рейтинга от 0 до 0.5:
            <input type="color" value={color0_5} onChange={(e) => setColor0_5(e.target.value)} />
          </label>
        </div>
        <div>
          <label>
            Цвет для рейтинга 5 и выше:
            <input type="color" value={color5} onChange={(e) => setColor5(e.target.value)} />
          </label>
        </div>
        <div>
          <label>
            Прозрачность:
            <input
              type="range"
              min="0"
              max="1"
              step="0.1"
              value={opacity}
              onChange={(e) => setOpacity(e.target.value)}
            />
          </label>
        </div>
        <button onClick={handleSave}>Сохранить настройки</button>
        <button onClick={handleImport}>Импортировать настройки</button>
        <button onClick={onUpdatePolygons}>Обновить полигоны</button>
      </div>
    </div>
  );
};

export default SettingsModal;
