import React, { useEffect, useState, useRef } from 'react';
import './App.css';

import  {handleFileInputChange, handleFileInputChange2}  from './addFileAZS'


const YandexMap = () => {
  const [mapInitiated, setMapInitiated] = useState(false);
  const mapRef = useRef(null);

  const objectsAdded1 = useRef(false);
  const stationPlacemarks = useRef([]);

  const [zoneRectangles, setZoneRectangles] = useState([]);
  const [objectsAdded, setObjectsAdded] = useState(false);


  useEffect(() => {
    const apiKey = 'a0325676-108c-4a26-8b08-8ddbcf9ee724';

    const initializeMap = () => {
      if (!window.ymaps) {
        const script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=${apiKey}&lang=ru_RU`;
        script.onload = () => {
          if (mapInitiated) return;

          const mapContainer = document.getElementById('map');
          if (mapContainer) {
            mapContainer.innerHTML = '';

            window.ymaps.ready(() => {
              mapRef.current = new window.ymaps.Map("map", {
                center: [55.76, 37.64],
                zoom: 10,
                controls: []
              });

              mapRef.current.controls.add('zoomControl', { position: { top: 10, left: 10 } });
              mapRef.current.controls.add('typeSelector', { position: { top: 10, right: 10 } });

              setMapInitiated(true);
            });
          }
        };

        if (!document.querySelector('script[src^="https://api-maps.yandex.ru/2.1/"]')) {
          document.body.appendChild(script);
        }
      }
    };

    initializeMap();
  }, [mapInitiated]);

  const handleStationButtonClick = () => {
    if (!objectsAdded1.current) {
      fetchStationData();
      objectsAdded1.current = true;
    } else {
      clearStations();
      objectsAdded1.current = false;
    }
  };


  const fetchStationData = () => {
    fetch('http://localhost:5000/api/Station')
      .then(response => response.json())
      .then(data => {
        data.forEach(coord => {
          const placemark = new window.ymaps.Placemark(
            [coord.latitude, coord.longitude],
            { hintContent: coord.address }
          );

          stationPlacemarks.current.push(placemark);
          mapRef.current.geoObjects.add(placemark);
        });
      })
      .catch(error => {
        console.error('Error fetching station data:', error);
      });
  };

  const handleZoneButtonClick = () => {
    if (!objectsAdded) {
      fetchZoneData();
      setObjectsAdded(true);
    } else {
      clearZones();
      setObjectsAdded(false);
    }
  };

  const clearZones = () => {
    if (mapRef.current) {
      zoneRectangles.forEach((rectangle) => {
        mapRef.current.geoObjects.remove(rectangle);
      });
      setZoneRectangles([]);
    }
  };

  const fetchZoneData = () => {
    fetch('http://localhost:5000/api/Zone')
      .then(response => response.json())
      .then(data => {
        const polygonLayers = data.map(item => {
          const { polygon, rating } = item;
          if (polygon && polygon.coordinates) {
            return processPolygon(polygon.coordinates, rating);
          } else {
            console.error('Некорректные данные полигона:', item);
            return null;
          }
        }).filter(layer => layer !== null);

        if (mapRef.current) {
          const newZoneRectangles = [];
          polygonLayers.forEach(layer => {
            mapRef.current.geoObjects.add(layer);
            newZoneRectangles.push(layer);
          });
          setZoneRectangles(newZoneRectangles);
        } else {
          console.error('Карта не инициализирована');
        }
      })
      .catch(error => {
        console.error('Ошибка при получении данных полигонов:', error);
      });
  };

  const processPolygon = (coordinates, rating) => {
    const color = getRatingColor(rating);
    try {
      return new window.ymaps.GeoObject({
        geometry: {
          type: 'Polygon',
          coordinates: coordinates.map(coord => coord.reverse()),
        },
        properties: {
          hintContent: `Рейтинг: ${rating}`,
          balloonContent: `Рейтинг: ${rating}`,
        },
      }, {
        fillColor: color,
        strokeColor: '#0000ff',
        opacity: 0.5,
      });
    } catch (error) {
      console.error('Ошибка при создании полигона:', error);
      return null;
    }
  };

  const getRatingColor = (rating) => {
    if (rating === 0) {
      return 'rgba(255, 0, 0, 0.5)'; // Красный цвет для рейтинга меньше 3
    } else if (rating > 0 && rating < 0.5) {
      return 'rgba(255, 255, 0, 0.5)'; // Желтый цвет для рейтинга от 3 до 5
    } else {
      return 'rgba(0, 255, 0, 0.5)'; // Зеленый цвет для рейтинга 5 и выше
    }
  };


  const clearStations = () => {
    stationPlacemarks.current.forEach((placemark) => {
      mapRef.current.geoObjects.remove(placemark);
    });
    stationPlacemarks.current = [];
  };


  return (
    <div className="body">
      <div className="header">Добавте ЭЗС:
      <input
          type="file"
          className="header-button"
          onChange={handleFileInputChange}

        />
        <br></br>
        Добавте зоны:
        <input
          type="file"
          className="header-button"
          onChange={handleFileInputChange2}

        />
      </div>
      <div className='content'>
      <div className="controls">
        <div className='baton' onClick={handleStationButtonClick}>Показать станции</div>
        <div className='baton' onClick={handleZoneButtonClick}>Показать зоны</div>
      </div>
      <div className="map" id="map"></div>
      </div>
    </div>
  );
};

export default YandexMap;
