export const handleStationButtonClick = (mapRef, stationPlacemarks, filters) => {
  // Сначала очищаем метки, чтобы не дублировать при следующем запросе.
  clearStations(mapRef, stationPlacemarks);

  // Теперь загружаем данные в зависимости от выбранных фильтров.
  fetchStationData(mapRef, stationPlacemarks, filters);
};

const fetchStationData = (mapRef, stationPlacemarks, filters) => {
  fetch('http://localhost:5000/api/Station')
      .then(response => response.json())
      .then(data => {
          // Фильтрация данных в зависимости от выбранных критериев.
          const filteredData = data.filter(coord => {
              const matchesAll = filters.includes('all');

              // Фильтры по количеству портов
              const connectorCriteria = [
                  filters.includes('connector1') && coord.connectors_total === 1,
                  filters.includes('connector2') && coord.connectors_total === 2,
                  filters.includes('connector3') && coord.connectors_total === 3
              ];

              // Фильтры по типу зарядной станции
              const matchesSlow = filters.includes('slow') && coord.evse_type === false; // Медленная зарядная станция
              const matchesFast = filters.includes('fast') && coord.evse_type === true; // Быстрая зарядная станция

              // Если выбрано "Показать все", то возвращаем true
              if (matchesAll) return true;

              // Условие для отображения зарядных станций
              const matchesConnectorCriteria = connectorCriteria.some(Boolean);
              const matchesTypeCriteria = (matchesSlow || matchesFast);

              if (matchesTypeCriteria && matchesConnectorCriteria){
                console.log("sadfasd")
               return matchesTypeCriteria || matchesConnectorCriteria 
              }

              if (matchesConnectorCriteria){
                return matchesConnectorCriteria
              }

              if(matchesTypeCriteria){
                return matchesTypeCriteria
              }

              return ;
          });

          // Добавляем метки на карту для отфильтрованных данных.
          filteredData.forEach(coord => {
              const placemark = new window.ymaps.Placemark(
                  [coord.latitude, coord.longitude],
                  { hintContent: coord.adress }
              );

              stationPlacemarks.current.push(placemark);
              mapRef.current.geoObjects.add(placemark);
          });
      })
      .catch(error => {
          console.error('Error fetching station data:', error);
      });
};

const clearStations = (mapRef, objectsAdded1) => {
  objectsAdded1.current.forEach((placemark) => {
      mapRef.current.geoObjects.remove(placemark);
  });
  objectsAdded1.current = [];
};
